// Render destinations on page load
function renderDestinations(destinationsList) {
    const destinationList = document.getElementById('destinationList');
    destinationList.innerHTML = ""; // Clear current destination list
    
    destinationsList.forEach(dest => {
        const card = document.createElement('div');
        card.classList.add('destination-card');
        card.innerHTML = `
            <img src="${dest.image}" alt="${dest.name}">
            <h3>${dest.name}</h3>
            <p>${dest.description}</p>
        `;
        destinationList.appendChild(card);
    });
}

// Search destinations based on user input
function searchDestinations() {
    const searchTerm = document.getElementById('searchInput').value.toLowerCase();
    const filteredDestinations = destinations.filter(dest => dest.name.toLowerCase().includes(searchTerm));
    renderDestinations(filteredDestinations);
}

// Initial render
renderDestinations(destinations);
